def transmissao (dia0,fatort,diast):
  dia = 1 
  infectados = dia0
  infectadost = dia0
  while dia <= diast:
    infectados *= fatort
    infectadost += infectados
    dia += 1
  return infectadost
print (transmissao (1,5,3))
print (transmissao (2,1,5))
